//----------------------------- // Recipe: Overload operators for custom behavior //----------------------------- #pragma strict_types class Complex { public float real; public float imag; void create(float r, float i) { real = r; imag = i; } // Overload + public Complex `+(mixed other) { if (!objectp(other) || object_program(other) != Complex) error("Can only add Complex to Complex
"); return Complex(real + other->real, imag + other->imag); } // Overload == public int `==(mixed other) { return objectp(other) && object_program(other) == Complex && real == other->real && imag == other->imag; } public string _sprintf(int type) { if (type == 'O' || type == 's') { return sprintf("Complex(%.2f%+.2fi)", real, imag); } return sprintf("%O", this); } } Complex c1 = Complex(3.0, 4.0); Complex c2 = Complex(1.0, 2.0); Complex sum = c1 + c2; write("Sum: %s
", sum); // Complex(4.00+6.00i) if (sum == Complex(4.0, 6.0)) { write("Equality works
